mapper.select:
SELECT id, name, grade_level_id AS gradeLevelId, major_id AS majorId, department_id AS departmentId FROM studentms.class


        name LIKE CONCAT('%', #{name}, '%')


        AND grade_level_id = #{gradeLevelId}


        AND major_id = #{majorId}


        AND department_id = #{departmentId}


ORDER BY id

mapper.update:
UPDATE studentms.class SET
name = #{class.name},
grade_level_id = #{class.gradeLevelId},
major_id = #{class.majorId},
department_id = #{class.departmentId}
WHERE id = #{class.id}
