mapper.select:
SELECT id, course_id AS courseId, teacher_id AS teacherId, term FROM studentms.course_open


        course_id = #{cid}


        AND teacher_id = #{tid}


        AND term = #{term}

SELECT c.id, c.cname, c.ccredit
FROM studentms.course c INNER JOIN studentms.course_open ct ON c.id = ct.course_id
WHERE ct.teacher_id = #{tid} AND ct.term = #{term}

SELECT c.id AS course_id, c.cname, t.id AS teacher_id, t.tname, c.ccredit
FROM studentms.course c INNER JOIN studentms.course_open ct ON c.id = ct.course_id
        INNER JOIN studentms.teacher t ON ct.teacher_id = t.id


        t.id = #{tid}



            AND t.tname = #{tname}


            AND t.tname LIKE CONCAT('%', #{tname}, '%')



        AND c.id = #{cid}



            AND c.cname = #{cname}


            AND c.cname LIKE CONCAT('%', #{cname}, '%')
