mapper.select:
SELECT id, operator, operation_type AS operationType, target_table AS targetTable, 
       target_id AS targetId, content, created_at AS createTime 
FROM studentms.operation_log WHERE id = #{id}

SELECT id, operator, operation_type AS operationType, target_table AS targetTable, 
       target_id AS targetId, content, created_at AS createTime 
FROM studentms.operation_log


        operator LIKE CONCAT('%', #{operator}, '%')


        AND operation_type = #{operationType}


        AND target_table = #{targetTable}


        AND created_at >= #{startTime}


        AND created_at <= #{endTime}


ORDER BY created_at DESC
