mapper.select:
SELECT sct.course_id AS cid, sct.course_name AS cname, sct.teacher_id AS tid, sct.teacher_name AS tname, 
       sct.student_id AS sid, s.sname, sct.grade, sct.term,
       sct.usual_grade, sct.final_grade, sct.total_grade, sct.class_id, sct.major_id, sct.department_id,
       sct.class_name, sct.major_name, sct.department_name, sct.grade_level,
       sct.course_category, sct.course_nature, sct.exam_method, sct.hours, sct.credit,
       t.tname as teacher_real_name
FROM studentms.score sct 
LEFT JOIN studentms.student s ON sct.student_id = s.id
LEFT JOIN studentms.teacher t ON sct.teacher_id = t.id


        sct.student_id = #{sid}


        AND sct.teacher_id = #{tid}


        AND sct.course_id = #{cid}


        AND s.sname LIKE CONCAT('%', #{sname}, '%')


        AND sct.teacher_name LIKE CONCAT('%', #{tname}, '%')


        AND sct.course_name LIKE CONCAT('%', #{cname}, '%')


        AND sct.term = #{term}


        AND sct.class_id = #{classId}


        AND sct.major_id = #{majorId}


        AND sct.department_id = #{departmentId}


        AND sct.class_name LIKE CONCAT('%', #{className}, '%')


        AND sct.major_name LIKE CONCAT('%', #{majorName}, '%')


        AND sct.department_name LIKE CONCAT('%', #{departmentName}, '%')


        AND sct.grade_level = #{gradeLevel}


        AND sct.total_grade >= #{lowBound}


        AND #{highBound} >= sct.total_grade
