mapper.select:
SELECT sct.course_id AS cid, c.cname, sct.teacher_id AS tid, t.tname, sct.student_id AS sid, s.sname, s.student_no, sct.grade, sct.term, sct.usual_score AS usualScore, sct.mid_score AS midScore, sct.final_score AS finalScore, sct.remark, s.class_id, s.major_id, s.department_id, cls.name AS class_name, m.name AS major_name, d.name AS department_name, gl.name AS grade_level, c.category AS course_category, c.nature AS course_nature, c.exam_method, c.hours, c.ccredit AS credit, t.tname as teacher_real_name FROM studentms.score sct LEFT JOIN studentms.student s ON sct.student_id = s.id LEFT JOIN studentms.teacher t ON sct.teacher_id = t.id LEFT JOIN studentms.course c ON sct.course_id = c.id LEFT JOIN studentms.class cls ON s.class_id = cls.id LEFT JOIN studentms.major m ON s.major_id = m.id LEFT JOIN studentms.department d ON s.department_id = d.id LEFT JOIN studentms.grade_level gl ON s.grade_level_id = gl.id
sct.student_id = #{sid}
AND sct.teacher_id = #{tid}
AND sct.course_id = #{cid}
AND s.sname LIKE CONCAT('%', #{sname}, '%')
AND t.tname LIKE CONCAT('%', #{tname}, '%')
AND c.cname LIKE CONCAT('%', #{cname}, '%')
AND sct.term = #{term}
AND s.class_id = #{classId}
AND s.major_id = #{majorId}
AND s.department_id = #{departmentId}
AND cls.name LIKE CONCAT('%', #{className}, '%')
AND m.name LIKE CONCAT('%', #{majorName}, '%')
AND d.name LIKE CONCAT('%', #{departmentName}, '%')
AND s.grade_level_id = #{gradeLevelId}
AND sct.grade IS NOT NULL AND sct.grade >= #{lowBound}
AND sct.grade IS NOT NULL AND sct.grade <= #{highBound}

mapper.insert:
INSERT INTO studentms.score (
    student_id, course_id, teacher_id, term, 
    usual_score, mid_score, final_score, grade, remark
)
VALUES

    (
        #{item.studentId}, #{item.courseId}, #{item.teacherId}, #{item.term}, 
        #{item.usualScore}, #{item.midScore}, #{item.finalScore}, #{item.grade}, #{item.remark}
    )
