mapper.select:
SELECT s.*, c.name AS class_name, gl.name AS grade_level_name, m.name AS major_name, d.name AS department_name FROM studentms.student s LEFT JOIN studentms.class c ON s.class_id = c.id LEFT JOIN studentms.grade_level gl ON s.grade_level_id = gl.id LEFT JOIN studentms.major m ON s.major_id = m.id LEFT JOIN studentms.department d ON s.department_id = d.id
s.id = #{student.id}
AND s.sname = #{student.sname}
AND s.sname LIKE CONCAT('%', #{student.sname}, '%')
